import { StackNavigationHelpers } from "@react-navigation/stack/lib/typescript/src/types";
import * as React from "react";
import {
  KeyboardAvoidingView,
  Text,
  Button,
  TextInput,
  StyleSheet,
} from "react-native";
import { useState } from "react";
import socket from "../socket";

interface CustomCardScreenProps {
  navigation: StackNavigationHelpers;
  route: any;
}

export default function CustomCardScreen({
  navigation,
  route,
}: CustomCardScreenProps) {
  const { code, name, isHost } = route.params;
  const [question, setQuestion] = useState("");

  const submitCardHandler = () => {
    socket.emit("custom", code, question, (data: any) => {
      console.log(data);
    });
  };

  const startGameHandler = () => {
    socket.emit("start-game", code, (data) => {
      const { current, card, users } = data;
      navigation.navigate("Game", {
        code,
        current,
        card,
        name,
        users,
        isHost,
      });
    });
  };

  const start = isHost ? (
    <Button title="Start game" onPress={startGameHandler} />
  ) : null;

  return (
    <KeyboardAvoidingView style={styles.screen} behavior="padding">
      <Text style={styles.bigText}>Add custom card to be read anonymously</Text>
      <TextInput
        multiline={true}
        style={styles.inputField}
        placeholder="Input question"
        placeholderTextColor="black"
        onChangeText={(text) => setQuestion(text)}
        value={question}
      />
      <Button title="Submit card" onPress={submitCardHandler} />
      {start}
    </KeyboardAvoidingView>
  );
}

const styles = StyleSheet.create({
  screen: {
    padding: 50,
    alignItems: "center",
    justifyContent: "center",
    flex: 1,
    backgroundColor: "black",
  },
  inputField: {
    padding: 5,
    marginTop: 10,
    // marginBottom: 20,
    width: 250,
    textAlign: "left",
    alignSelf: "center",
    borderBottomWidth: 1,
    borderColor: "white",
    fontSize: 18,
    color: "white",
    fontFamily: "Avenir-Light",
  },
  bigText: {
    fontFamily: "Avenir-Light",
    fontSize: 30,
    color: "white",
    textAlign: "center",
  },
});
