import React from "react";
import { StackNavigationHelpers } from "@react-navigation/stack/lib/typescript/src/types";
import {
  View,
  Text,
  StyleSheet,
  TextInput,
  Modal,
  KeyboardAvoidingView,
} from "react-native";
import { useState } from "react";
import { TouchableOpacity } from "react-native-gesture-handler";
import socket from "../socket";

import { HostButton, JoinButton, HelpButton } from "../assets/images/";

interface HomeScreenProps {
  navigation: StackNavigationHelpers;
  route: any;
}

export default function HomeScreen({ navigation, route }: HomeScreenProps) {
  const [modalVisible, setModalVisible] = useState(false);
  const [code, setCode] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const { name } = route.params;

  const hostGameHandler = () => {
    socket.emit("create", name, (code: number) => {
      navigation.navigate("Waiting", {
        name,
        code,
        users: [name],
        isHost: true,
        happy: true,
        heavy: true,
        toTheSpeaker: true,
        selfReflection: true,
        customCards: false,
      });
    });
  };

  const joinGameHandler = () => {
    if (code.length !== 5) {
      setErrorMessage("Please enter a valid 5-digit code.");
      return;
    }

    socket.emit("join", name, code, (data: any) => {
      const { ok, users, gameStarted, message, settings } = data;
      if (ok) {
        if (gameStarted) {
          navigation.navigate("Game", {
            name,
            code,
            users,
            isHost: false,
            ...settings,
          });
        } else {
          navigation.navigate("Waiting", {
            name,
            code,
            users,
            isHost: false,
            ...settings,
          });
        }
      } else {
        setErrorMessage(message);
      }
    });
  };

  return (
    <KeyboardAvoidingView style={styles.screen} behavior="padding">
      <View style={styles.itemContainer}>
        <View style={styles.container}>
          <Text style={styles.bigText}>Host Confessation</Text>
          <TouchableOpacity onPress={hostGameHandler}>
            <HostButton width={250} />
          </TouchableOpacity>
        </View>
        <View style={styles.container}>
          <Text style={styles.bigText}>Join Confessation</Text>
          <TextInput
            placeholder="Enter a room code"
            placeholderTextColor="white"
            onChangeText={(text) => setCode(text)}
            value={code}
            keyboardType="number-pad"
            style={styles.inputField}
          />
          <Text style={{ ...styles.smallText, color: "red" }}>
            {errorMessage}
          </Text>
          <TouchableOpacity style={{ marginTop: 30 }} onPress={joinGameHandler}>
            <JoinButton width={250} />
          </TouchableOpacity>
        </View>
      </View>
      <View style={styles.helpContainer}>
        <Modal animationType="slide" transparent={true} visible={modalVisible}>
          <View style={styles.modalContainer}>
            <View style={styles.modalView}>
              <View style={{ flex: 9 }}>
                <Text style={styles.bigText}>Our Mission:</Text>
                <Text style={styles.smallText}>
                  Heart 2 Heart Confessations was built with the intention of
                  fostering meaningful connections with your peers. It allows
                  users to deepen existing relationships as well as form new
                  ones.
                </Text>
                <Text style={styles.smallText}>
                  Here’s our suggestion on how to use this app:
                </Text>
                <Text style={styles.smallText}>
                  1. Find people you’d like to know better; whether it be your
                  friends, family, or significant other.
                </Text>
                <Text style={styles.smallText}>
                  2. Arrange a group call on another device.
                </Text>
                <Text style={styles.smallText}>
                  3. Host a confessation room and send the code to your peers.
                </Text>
                <Text style={styles.smallText}>
                  4. The person who’s turn it is can pose the question on the
                  screen to the rest of the group.
                </Text>
                <Text style={styles.smallText}>
                  5. Answer honestly and let everyone have a chance to speak and
                  listen.
                </Text>
              </View>
              <View style={styles.closeContainer}>
                <TouchableOpacity
                  onPress={() => {
                    setModalVisible(!modalVisible);
                  }}
                >
                  <Text style={styles.smallText}>Close</Text>
                </TouchableOpacity>
              </View>
            </View>
          </View>
        </Modal>
        <TouchableOpacity
          onPress={() => {
            setModalVisible(!modalVisible);
          }}
        >
          <HelpButton />
        </TouchableOpacity>
      </View>
    </KeyboardAvoidingView>
  );
}

const styles = StyleSheet.create({
  screen: {
    padding: 50,
    alignItems: "center",
    justifyContent: "center",
    flex: 1,
    backgroundColor: "black",
  },
  helpContainer: {
    flexDirection: "row",
    flex: 1,
    alignSelf: "flex-end",
    marginTop: 30,
  },
  container: {
    padding: 10,
    marginVertical: 20,
    alignItems: "center",
  },
  itemContainer: {
    flex: 4,
  },
  modalContainer: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    margin: 10,
    padding: 20,
  },
  modalView: {
    margin: 20,
    backgroundColor: "black",
    borderRadius: 20,
    borderWidth: 1,
    borderColor: "white",
    padding: 20,
    // justifyContent: "center",
    height: 600,
    width: 350,
    opacity: 0.9,
    alignItems: "center",
  },
  closeContainer: {
    marginTop: 50,
    justifyContent: "flex-end",
    borderWidth: 1,
    borderColor: "white",
    borderRadius: 20,
    paddingHorizontal: 10,
  },
  bigText: {
    fontSize: 30,
    color: "white",
    paddingBottom: 10,
    fontFamily: "Avenir-Light",
  },
  inputField: {
    padding: 5,
    marginTop: 10,
    width: 250,
    textAlign: "left",
    alignSelf: "center",
    borderBottomWidth: 1,
    borderColor: "white",
    fontSize: 18,
    color: "white",
    fontFamily: "Avenir-Light",
  },
  smallText: {
    fontSize: 14,
    color: "white",
    textAlign: "center",
    padding: 5,
  },
  continueText: {
    fontFamily: "Avenir-Light",
    fontSize: 14,
    color: "#892cdc",
    paddingVertical: 30,
  },
});
